INSERT INTO instruction VALUES(293,2,'');
INSERT INTO instruction VALUES(293,3,'Add tomatoes & yogurt.  Stir in salt & cook for 2 minutes.');
INSERT INTO instruction VALUES(293,4,'');
INSERT INTO instruction VALUES(293,5,'Pour in water & bring to a boil.  Reduce heat & simmer for 10 minutes. The gravy is now ready to use.');
