INSERT INTO instruction VALUES(401,9,'Bruce Cost, "Ginger East to West"');
INSERT INTO instruction VALUES(402,1,'In a medium pot, cook quartered potatoes in salted water till tender.');
INSERT INTO instruction VALUES(402,2,'');
INSERT INTO instruction VALUES(402,3,'Meanwhile, combine remaining ingredients in a small bowl.  Drain potatoes, add to the bowl & toss gently.  Serve warm or at room temperature or cold.');
