INSERT INTO instruction VALUES(403,1,'In a bowl, dissolve yeast in water.  Add salt & oil.  Beat in flour, 1 c at a time.  Use your fingers to ensure that the ingredients have been thoroughly mixed.');
INSERT INTO instruction VALUES(403,2,'');
INSERT INTO instruction VALUES(403,3,'Bang down the dough on a counter surface.  Shape into a small round bread & place in a 9" round pan sprinkled with cornmeal.  Allow to rise till doubled.  Preheat oven to 375F & bake for 35 to 45 minutes.');
