INSERT INTO instruction VALUES(405,1,'In a blender combine all ingredients except dill, chives, and garlic chives, and process until smooth.  Stir in herbs and serve.');
INSERT INTO instruction VALUES(405,2,'');
INSERT INTO instruction VALUES(405,3,'"Great on top of potatoes or with raw vegetables, vegetable burritos, tacos, or scrambled tofu - anywhere you might use sour cream."');
