INSERT INTO instruction VALUES(405,5,'Source: Liz Lugan - Clawson, Michigan The Herb Companion - February/March 1993 Typed for you by Karen Mintzias');
INSERT INTO instruction VALUES(406,1,'This dish is a speciality of Narind nad Warochun at their restaurant The Lemon Grass, Sukhumvit Soi 24, Bangkok.');
INSERT INTO instruction VALUES(406,2,'');
INSERT INTO instruction VALUES(406,3,'In a large pan of boiling water, blanch the noodles and set aside.  Blanch the beansprouts, long beans and broccoli stems, arrange on a serving dish and set aside.');
INSERT INTO instruction VALUES(406,4,'');
INSERT INTO instruction VALUES(406,5,'In a saucepan, gently heat the coconut milk, stirring in the red curry paste until thoroughly blended.  Add the remaining ingredients, stirring until mixed.  Turn into a serving bowl.');
INSERT INTO instruction VALUES(406,6,'');
INSERT INTO instruction VALUES(406,7,'Either serve all three elements separately or place the noodles on the serving dish with the vegetables, pour over the sauce and garnish with the crispy potato.');
INSERT INTO instruction VALUES(406,8,'');
INSERT INTO instruction VALUES(406,9,'Source: Thai Vegetarian Cooking by Vatcharin Bhumichitr Typed for you by Karen Mintzias');
INSERT INTO instruction VALUES(407,1,'Mix all but the nori sheets together.');
INSERT INTO instruction VALUES(407,2,'');
INSERT INTO instruction VALUES(407,3,'Let the mixture sit one or more hours to absorb the flavors.');
INSERT INTO instruction VALUES(407,4,'');
INSERT INTO instruction VALUES(407,5,'Set out the toasted nori sheets. Place 3/4 cup filling on each sheet of toasted nori. Roll tightly and cut into five slices.');
INSERT INTO instruction VALUES(407,6,'');
