INSERT INTO instruction VALUES(517,1,'Mix tamari with paprika and cayenne pepper in a bowl and then stir in grated tempeh. Spread on large plate and microwave for 5-10 min. (Or bake or broil until firmer and browner).');
INSERT INTO instruction VALUES(517,2,'');
INSERT INTO instruction VALUES(517,3,'Chop fine the peppers and pickle and mix with browned tempeh. Bring 3 cups of vegetarian gelatin (with or without bouillon) to boil, pour over the pepper/pickle/tempeh mix and then pour into flat dish to 1/2 inch height.');
