INSERT INTO instruction VALUES(529,3,'Rani, "Feast of India"');
INSERT INTO instruction VALUES(530,1,'Cut off and discard tough mushroom stems and cut caps into wedges. Heat oil in skillet. Add mushrooms and stir-fry until almost tender. Add mirin, soy sauce and sugar and toss to coat mushrooms.');
