INSERT INTO instruction VALUES(531,1,'Cut the eggplant into thin 1-1/2-inch by 3-inch slices.  Do not peel them.');
INSERT INTO instruction VALUES(531,2,'');
INSERT INTO instruction VALUES(531,3,'For the batter mix the flour, water, and salt together in a small bowl, then strain through a fine sieve.  Let rest for about 20 minutes.');
