BEGIN TRANSACTION;
-- DROP TABLE IF EXISTS recipes;
-- DROP TABLE IF EXISTS category;
-- DROP TABLE IF EXISTS categories;
-- DROP TABLE IF EXISTS ingredient;
-- DROP TABLE IF EXISTS ingredients;
-- DROP TABLE IF EXISTS instruction;
-- DROP TABLE IF EXISTS ingredientsection;
-- DROP TABLE IF EXISTS instructionsection;
CREATE TABLE IF NOT EXISTS recipes(id INTEGER PRIMARY KEY, title VARCHAR NOT NULL, servings INTEGER NOT NULL, servingsunit VARCHAR NOT NULL, imageURL VARCHAR NOT NULL);
CREATE TABLE IF NOT EXISTS categories(id INTEGER PRIMARY KEY, name VARCHAR UNIQUE NOT NULL);
CREATE TABLE IF NOT EXISTS category(recipeid INTEGER NOT NULL, categoryid INTEGER NOT NULL, PRIMARY KEY(recipeid, categoryid), FOREIGN KEY(recipeid) REFERENCES recipes(id), FOREIGN KEY(categoryid) REFERENCES categories(id));
CREATE TABLE IF NOT EXISTS ingredients(id INTEGER PRIMARY KEY, name VARCHAR UNIQUE NOT NULL, price REAL NOT NULL);
CREATE TABLE IF NOT EXISTS ingredient(recipeid INTEGER NOT NULL, line INTEGER NOT NULL, amountint INTEGER NOT NULL, amountnum INTEGER NOT NULL, amountdenom INTEGER NOT NULL, amountfloat REAL NOT NULL, unit CHARACTER(2) NOT NULL, ingredientid INTEGER NOT NULL, PRIMARY KEY(recipeid, line), FOREIGN KEY(recipeid) REFERENCES recipes(id), FOREIGN KEY(ingredientid) REFERENCES ingredients(id));
CREATE TABLE IF NOT EXISTS instruction(recipeid INTEGER NOT NULL, line INTEGER NOT NULL, txt TEXT NOT NULL, PRIMARY KEY(recipeid, line), FOREIGN KEY(recipeid) REFERENCES recipes(id));
CREATE TABLE IF NOT EXISTS ingredientsection(recipeid INTEGER NOT NULL, line INTEGER NOT NULL, title VARCHAR(60) NOT NULL, PRIMARY KEY (recipeid, line), FOREIGN KEY(recipeid) REFERENCES recipes(id));
CREATE TABLE IF NOT EXISTS instructionsection(recipeid INTEGER NOT NULL, line INTEGER NOT NULL, title VARCHAR NOT NULL, PRIMARY KEY (recipeid, line), FOREIGN KEY(recipeid) REFERENCES recipes(id));
COMMIT;
